"use client";
import { shiftWeekNames as s } from "../../utils.mjs";
class r {
  constructor(e) {
    this.intl = e;
  }
  getWeekNames(e = !1) {
    const t = s(
      this.intl.dateFormatNames({ nameType: "short", type: "days" }),
      this.intl.firstDay()
    );
    return e ? [""].concat(t) : t;
  }
}
export {
  r as WeekNamesService
};
