  return [g, M];
}, m = (e) => (t, s, n) => {
  const [c, o] = e(n[s - 1], n[s - 2], t), [h, l] = e(t, n[s - 1], n[s + 1], !0);
  return `C ${c},${o} ${h},${l} ${t[0]},${t[1]}`;
};
